mapper.select:
SELECT * FROM `file_info`  WHERE file_name ${other} REGEXP #{suffix}  AND is_current=1 ORDER BY CONVERT(${order} USING gbk)
                                                                              COLLATE gbk_chinese_ci ${sequence}
                                                                              LIMIT ${current}, ${size};

SELECT * FROM `file_info` WHERE file_name LIKE #{fileName} AND is_current=1 ORDER BY CONVERT(${order} USING gbk)
                                                                    COLLATE gbk_chinese_ci ${sequence}
                                                                    LIMIT ${current}, ${size};
