mapper.select:
SELECT * FROM `file_info`  WHERE file_name ${other} REGEXP #{suffix} ORDER BY CONVERT(${order} USING gbk)
                                                                              COLLATE gbk_chinese_ci ${sequence}
                                                                              LIMIT ${current}, ${size};

SELECT * FROM `file_info` WHERE file_name LIKE #{fileName} ORDER BY CONVERT(${order} USING gbk)
                                                                    COLLATE gbk_c hinese_ci ${sequence}
                                                                    LIMIT ${current}, ${size};
